const SeuNome = () => {

  const [nome, setName] = useState();

  setName = (name) => {
    e.preventDefault()
    console.log ({name})
  }

  return (

    <div>
      <p>Digite seu nome</p>

      <input
        type="text"
        placeholder="Qual é o seu nome?"
      />
      <input type="submit" value="Enviar" onSubmit={(e) => setName(e.target.value)}></input>
    </div>
  )

}

export default SeuNome
