const Evento = ({numero}) => {
  const meuEvento = () => {
    console.log(`"Opa! Fui ativado", ${numero}`)
  }

  const meuBotao = () => {
    <button onClick={meuEvento}>Ativar</button>
  }
  return (
    <>
      <p>Clique para disparar um evento</p>
      <Button />
    </>
  )
}

export default Evento
