import { useState } from "react";

const Condicional = () => {

  const enviarEmail = (e) => {
    e.preventDefault()
    console.log("Email cadastrado")
  }

  return (
    <div>
      <h2>Cadastre seu email</h2>
      <form>
        <input type="email" placeholder="Digite seu email" />
        <button onClick={enviarEmail}>Enviar</button>
      </form>
    </div>
  )
}

export default Condicional
